select group_concat(distinct tr2.numeracao_tipo_resultado) as numeracao1, group_concat(distinct tr1.numeracao_tipo_resultado) as numeracao2, group_concat(distinct tr1.nome_tipo_resultado) as nome_tipo_resultado1, group_concat(distinct tr2.nome_tipo_resultado) as nome_tipo_resultado2, group_concat(nome_token order by ordem separator " ") from tokens, frases, tipos_resultados as tr1, tipos_acoes, tipos_resultados as tr2 where id_token = id_chave_token and id_tipo_acao = id_chave_tipo_acao and id_tipo_resultado = tr1.id_chave_tipo_resultado and tr1.id_tipo_resultado_pai = tr2.id_chave_tipo_resultado group by id_tipo_acao order by numeracao1, numeracao2;
